import styled from "styled-components"
import { IoExitOutline,IoAddCircleOutline,IoRemoveCircleOutline } from 'react-icons/io5'
import { Link,useNavigate } from "react-router-dom";
import { useContext,useState } from "react";
import UserContext from "../contexts/UserContext";
import Content from "./Content";

export default function InitalPage(){
    const { user,setUser } = useContext(UserContext);
    const [records, setRecords] = useState([]);
    const navigate = useNavigate();

    function logOut(){
        setUser();
        navigate('/');
    }
    /*  <h2>Não há registros de entrada ou saída!</h2> */
    return(
        <Container>
            <header>
                <h1>Olá, {user}</h1>
                <IoExitOutline size={40} color={'#ffffff'} onClick={logOut} />
            </header>
            <Contents>
               <Content records={records} setRecords={setRecords}></Content>
            </Contents>
            <Section>
                <div>
                    <Link to="/add" style={{textDecoration:'none', color:'#ffffff'}}>
                        <IoAddCircleOutline size={22}/>
                    </Link>
                    <h3>Nova entrada</h3>
                </div>
                <div>
                    <Link to="/remove" style={{textDecoration:'none', color:'#ffffff'}}>
                        <IoRemoveCircleOutline size={22}/>
                    </Link>
                    <h3>Nova saída</h3>
                </div>
            </Section>
        </Container>
    );
}

const Container = styled.div`
    display: flex;
    align-items: center;
    flex-direction: column;
    width: 100%;
    height:915px;
    padding-left: 24px;
    padding-right: 24px;
    padding-top: 24px;
    padding-bottom: 16px;
    background-color: #8C11BE;

    header{
        width: 100%;
        display: flex;
        justify-content: space-between;
        align-items: center;

        h1{
            font-size: 26px;
            color: #FFFFFF;
        }
    }

    h3{
        margin-top: 36px;
        font-size: 16px;
        color: #FFFFFF;
    }
`

const Contents = styled.div`
    display: flex;
    justify-content: center;
    flex-direction: column;
    background-color: #ffffff;
    margin-top:22px;
    width: 100%;
    height: 446px;

    h2{
        width: 180px;
        height: 46px;
        font-size: 20px;
        color:#868686;
        text-align: center;
    }
`

const Section = styled.div`
    display: flex;
    justify-content: space-between;
    width: 100%;
    height: 114px;
    margin-top: 14px;

    div{
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        width: 48%;
        background-color: #A328D6;
        border: none;
        border-radius: 6px;
        padding: 8px;

        color: #FFFFFF;

        h3{
            font-size: 18px;
            width: 44px;
            height: 40px;
        }
    }
`
