    const moment = dayjs(Date.now()).format('DD/MM');
    const [records, setRecords] = useState();
    const { token } = useContext(UserContext);

    useEffect(()=>{
        const promise = axios.get("http://localhost:5000/initialpage",
        {
        headers:{
            user: token
        }
    })
    },[]);

    return(
        <Container>
            <div>
                <h6>{moment}</h6>
                <h4>Almoço mãe</h4>
            </div>
            <h5>39,90</h5>  
