import axios from 'axios'
import styled from 'styled-components'
import dayjs from 'dayjs'
import { useEffect,useContext } from 'react';
import UserContext from '../contexts/UserContext';

export default function Content({records,setRecords}){

    const { token } = useContext(UserContext);

    useEffect(()=>{
        const promise = axios.get("http://localhost:5000/initialpage",
        {
        headers:{
            user: token
        }
    });

    promise.then(res => {
        /* setRecords(res.data); */
    })

    promise.catch(Error => {
        alert(Error.response.data);
    })

    },[]);

    return(
        records.map((record,index) => 
        <Container key={index} price={record.price}>
            <div>
                <h6>{dayjs(record.time).format('DD/MM')}</h6>
                <h4>{record.description}</h4>
            </div>
            <h5>{record.price}</h5>  
        </Container>
    )
    );
}

const Container = styled.div`
    width: 100%;
    height: 440px;
    display: flex;
    justify-content: space-between;
    font-size: 16px;
    padding: 24px 12px 24px 12px;
    overflow-y: scroll;

    div{
        display: flex;
        justify-content: center;
    }

    h6{
        color: #C6C6C6;
        margin-right: 8px;
    }

    h4{
        color:#000000;
    }

    h5{
        color: ${props => props.price > 0 ? 'green' : 'red'};
    }
`
