import styled from "styled-components"
import { IoExitOutline,IoAddCircleOutline,IoRemoveCircleOutline } from "react-icons/io5"
import { Link,useNavigate } from "react-router-dom";
import { useContext,useState,useEffect } from "react";
import axios from "axios";
import UserContext from "../contexts/UserContext";
import Content from "./Content";

export default function InitalPage(){
    const { user,setUser,token,balance,setBalance } = useContext(UserContext);
    const [records, setRecords] = useState([]);
    const navigate = useNavigate();

    useEffect(()=>{
        const promise = axios.get("http://localhost:5000/initialpage",
        {
        headers:{
            user: token
        }
    });

    promise.then(res => {
        let sum = 0;
        setRecords([...res.data]);
        records.forEach(record => {
            sum+=record.price
        });
        setBalance(sum)
    })

    promise.catch(Error => {
        alert(Error.response.data);
    })

    },[balance]);

    function logOut(){
        setUser();
        navigate('/');
    }

    return(
        <Container>
            <header>
                <h1>Olá, {user}</h1>
                <IoExitOutline size={40} color={'#ffffff'} onClick={logOut} />
            </header>
            <Contents records={records} balance={balance}>
                <div>
                    {
                        records.length === 0 ? <h2>Não há registros de entrada ou saída!</h2>
                        :
                        <Content records={records} setRecords={setRecords}></Content>
                    }
                </div>
                {
                    records.length === 0 ? null
                    :
                    <div>
                        <h1>Saldo</h1>
                        <h5>{balance}</h5>
                    </div>
                }
            </Contents>
            <Section>
                <div>
                    <Link to="/add" style={{textDecoration:'none', color:'#ffffff'}}>
                        <IoAddCircleOutline size={22}/>
                    </Link>
                    <h3>Nova entrada</h3>
                </div>
                <div>
                    <Link to="/remove" style={{textDecoration:'none', color:'#ffffff'}}>
                        <IoRemoveCircleOutline size={22}/>
                    </Link>
                    <h3>Nova saída</h3>
                </div>
            </Section>
        </Container>
    );
}

const Container = styled.div`
    display: flex;
    align-items: center;
    flex-direction: column;
    width: 100%;
    height:915px;
    padding-left: 24px;
    padding-right: 24px;
    padding-top: 24px;
    padding-bottom: 16px;
    background-color: #8C11BE;

    header{
        width: 100%;
        display: flex;
        justify-content: space-between;
        align-items: center;

        h1{
            font-size: 26px;
            color: #FFFFFF;
        }
    }

    h3{
        margin-top: 36px;
        font-size: 16px;
        color: #FFFFFF;
    }
`

const Contents = styled.div`
    display: flex;
    justify-content: ${props => props.records.length === 0 ? 'center' : 'space-between'};;
    align-items: ${props => props.records.length === 0 ? 'center' : 'stretch'};
    flex-direction: column;
    background-color: #ffffff;
    margin-top:22px;
    width: 100%;

    h2{
        width: 180px;
        height: 46px;
        font-size: 20px;
        color:#868686;
        text-align: center;
    }

    > * {
      &:first-child {
        display: flex;
        justify-content: ${props => props.records.length === 0 ? 'center' : 'space-between'};
        align-items: ${props => props.records.length === 0 ? 'center' : 'stretch'};
        width: 100%;
        height: 410px;
        overflow-y: scroll;
      }
    }

    > * {
      &:last-child {
         display: flex;
         width: 100%;
         justify-content: space-between;
         padding-left: 12px;
         padding-right: 12px;
         padding-bottom: 12px;

         h1{
            color: #000000;
            font-size: 18px;
            font-style: bold;
         }

         h5{
            color: ${props => props.balance > 0 ? 'green' : 'red'};
        }
      }
    }
`

const Section = styled.div`
    display: flex;
    justify-content: space-between;
    width: 100%;
    height: 114px;
    margin-top: 14px;

    div{
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        width: 48%;
        background-color: #A328D6;
        border: none;
        border-radius: 6px;
        padding: 8px;

        color: #FFFFFF;

        h3{
            font-size: 18px;
            width: 44px;
            height: 40px;
        }
    }
`
