import styled from "styled-components"
import { IoExitOutline,IoAddCircleOutline,IoRemoveCircleOutline } from 'react-icons/io5'
import { Link } from "react-router-dom";

export default function InitalPage(){
    return(
        <Container>
            <header>
                <h1>Olá, Fulano</h1>
                <IoExitOutline size={24} color={'#ffffff'} />
            </header>
            <Content>
                <h2>Não há registros de entrada ou saída!</h2>
            </Content>
            <Section>
                <div>
                    <Link to="/add" style={{color:'none'}}>
                        <IoAddCircleOutline size={22}/>
                    </Link>
                    <h3>Nova entrada</h3>
                </div>
                <div>
                    <IoRemoveCircleOutline size={22}/>
                    <h3>Nova saída</h3>
                </div>
            </Section>
        </Container>
    );
}

const Container = styled.div`
    display: flex;
    align-items: center;
    flex-direction: column;
    width: 100%;
    height:915px;
    padding-left: 24px;
    padding-right: 24px;
    padding-top: 24px;
    padding-bottom: 16px;
    background-color: #8C11BE;

    header{
        width: 100%;
        display: flex;
        justify-content: space-between;
        align-items: center;

        h1{
            font-size: 26px;
            color: #FFFFFF;
        }
    }


    h3{
        margin-top: 36px;
        font-size: 16px;
        color: #FFFFFF;
    }
`

const Content = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: row;
    background-color: #ffffff;
    margin-top:22px;
    width: 100%;
    height: 446px;

    h2{
        width: 180px;
        height: 46px;
        font-size: 20px;
        color:#868686;
        text-align: center;
    }
`

const Section = styled.div`
    display: flex;
    justify-content: space-between;
    width: 100%;
    height: 114px;
    margin-top: 14px;

    div{
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        width: 48%;
        background-color: #A328D6;
        border: none;
        border-radius: 6px;
        padding: 8px;

        color: #FFFFFF;

        h3{
            font-size: 18px;
            width: 44px;
            height: 40px;
        }
    }
`
